catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
   if (isLoading || newValue == '') {
      return;
   }

	var sap_env = g_form.getValue("sap_system_environment");
	g_form.clearOptions("sap_box");

	g_form.addOption("sap_box", " ", " ");

	switch(sap_env) {
		case "production":
			g_form.addOption("sap_box", "AMP STRIPES", "AMP - NA STRIPES");
			g_form.addOption("sap_box", "APP STRIPES", "APP - SEA STRIPES");
			g_form.addOption("sap_box", "EUP STRIPES", "EUP - EU STRIPES");
			g_form.addOption("sap_box", "G9P GEMS", "G9P - GEMS");
			g_form.addOption("sap_box", "G3P IPES", "G3P - IPES");
			g_form.addOption("sap_box", "S8P NAPES", "S8P - NAPES");
			g_form.addOption("sap_box", "XTP XTO", "XTP - XTO");
			g_form.addOption("sap_box", "F2P CARS", "F2P - CARS");
			break;

		case "acceptance":
			g_form.addOption("sap_box", "AMB STRIPES", "AMB - NA STRIPES");
			g_form.addOption("sap_box", "APB STRIPES", "APB - SEA STRIPES");
			g_form.addOption("sap_box", "EUB STRIPES", "EUB - EU STRIPES");
			g_form.addOption("sap_box", "G9R GEMS", "G9R - GEMS");
			g_form.addOption("sap_box", "G3B IPES", "G3B - IPES");
			g_form.addOption("sap_box", "S8B NAPES", "S8B - NAPES");
			g_form.addOption("sap_box", "XTA XTO", "XTA - XTO");
			g_form.addOption("sap_box", "F2A CARS", "F2A - CARS");
			break;

		default:	
}

}
